import React, { useState } from 'react';
import { Container, Form, Button, Alert } from 'react-bootstrap';
import courseService from '../services/courseService';
import { useNavigate } from 'react-router-dom';

const CreateCourse = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const onSuccess = location.state?.onSuccess;
  const [course, setCourse] = useState({
    code: '',
    title: '',
    description: '',
    credits: 1
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setCourse({ ...course, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await courseService.createCourse(course);
      setSuccess('Course created successfully!');
      setError('');
      setTimeout(() => navigate('/admin'), 1000); // redirect to admin panel
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create course');
      setSuccess('');
    }
  };

  return (
    <Container>
      <h1>Create New Course</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Course Code</Form.Label>
          <Form.Control name="code" value={course.code} onChange={handleChange} required />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Title</Form.Label>
          <Form.Control name="title" value={course.title} onChange={handleChange} required />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Description</Form.Label>
          <Form.Control as="textarea" name="description" value={course.description} onChange={handleChange} />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Credits</Form.Label>
          <Form.Control type="number" name="credits" value={course.credits} min="1" onChange={handleChange} required />
        </Form.Group>
        <Button type="submit">Create Course</Button>
      </Form>
    </Container>
  );
};

export default CreateCourse;
